function HomePage() {   

    const [mostrarCont, setMostrarCont] = useState(false);
    const [angulo, setAngulo] = useState(0);
    const [tituloTerminou, setTituloTerminou] = useState(false);

    useEffect (() => {
        const intervalo = setInterval(() => {
            setAngulo(prev => (prev === 45 ? -45 : 45));
        }, 500)

        return () => {
            clearInterval(intervalo);
        }
    }, [])

    useEffect(() => {
        const esperar = setTimeout(() => {
            setMostrarCont(true)
        }, 500);

        return () => clearTimeout(esperar);
    }, []);

    if (!mostrarCont) return null;

    return (
        <main className='homePage'>
            <section className='homePage-Introducao'>
                <motion.div
                    initial={{ opacity: 0, scale: 0 }}
                    animate={{ opacity: 1, scale: 1 }}
                    exit={{ opacity: 1, scale: 1 }}
                    className='janela'
                >
                    <JanelaBarra guiaNome='Mika.exe'/>
                    <motion.div
                        animate = {{ rotate: angulo }}
                        transition={{ duration: 25 }}
                        className='mikaAscii'
                        >
                    <AsciiWrite label="Icone de Aranha representando Deky, uma aranha sorrindo com maria chiquinhas azuis" text={Mika}/>
                    </motion.div>
                </motion.div>
                <h1 className='titulo'>
                    <Typewriter 
                        onInit={(typewriter) => {
                            typewriter.typeString("Olá, eu sou o Deky!")
                            .pauseFor(250)
                            .callFunction(() => {setTituloTerminou(true)})
                            .start()
                        }}
                        options={{
                            cursor: '',
                            delay: 25,
                            autoStart: true
                        }}
                    />
                </h1>
                {tituloTerminou &&
                    <Typewriter options={{
                        strings: "<p class='texto'> Sou um estudante de 19 anos dos cursos de Análise e Desenvolvimento de Sistemas e Engenharia de Software.</p><p class='texto'>Através desse pequeno site você poderá dar uma olhada nas minhas criações e um pouco da minha personalidade.</p>",
                        cursor: '',
                        autoStart: true,
                        delay: 25
                    }}/>
                }
            </section>
        </main>
    )
}

export default HomePage